const { assert, expect } = require("chai");
const { getNamedAccounts, deployments, ethers, network } = require("hardhat");
const {
  isCallTrace,
} = require("hardhat/internal/hardhat-network/stack-traces/message-trace");
const {
  developmentChains,
  networkConfig,
} = require("../helper-hardhat-config");

const lowSVGImageuri =
  "data:image/svg+xml;base64,";
const highSVGimageUri =
  "data:image/svg+xml;base64,";

const highTokenUri =
  "data:application/json;";
const lowTokenUri =
  "data:application/json;";

if (developmentChains.includes(network.name)) {
  describe("SVG NFT Unit Testing", function () {
    let dynamicSvgNft, mockv3Aggregator, deployer;

    beforeEach(async () => {
      accounts = await ethers.getSigners();
      deployer = accounts[0];
      await deployments.fixture(["dynamicsvg", "mocks"]);
      dynamicSvgNft = await ethers.getContract("DynamicSvgNft");
      mockv3Aggregator = await ethers.getContract("MockV3Aggregator");
    });

    describe("Constructor", async () => {
      it("creates constructor events properly", async () => {
        const getPriceFeed = await dynamicSvgNft.getPriceFeed();
        const getLowSvgUri = await dynamicSvgNft.getLowUri();
        const getHighSvgUri = await dynamicSvgNft.getHighUri();
        const tokenCounter = await dynamicSvgNft.getTokenCounter();

        assert.equal(getPriceFeed, mockv3Aggregator.address);
        assert.equal(lowSVGImageuri, getLowSvgUri);
        assert.equal(highSVGimageUri, getHighSvgUri);
        assert.equal(tokenCounter, 0);
      });
    });

    describe("mintNft", async () => {
      it("Gives the high token URI when price entered is less than price of eth", async () => {
        const highValue = ethers.utils.parseEther("1");
        let mintNft = await dynamicSvgNft.mintNft(highValue);
        let tokenCounter = await dynamicSvgNft.getTokenCounter();

        await expect(mintNft).to.emit(dynamicSvgNft, "CreatedNFT");
        assert.equal(tokenCounter.toString(), "1");

        const tokenURI = await dynamicSvgNft.tokenURI(1);
        assert.equal(tokenURI, highTokenUri);
      });

      it("Gives the low token URI when price entered is above than price of eth", async () => {
        const highValue = ethers.utils.parseEther("10000000");
        const mintNft = await dynamicSvgNft.mintNft(highValue);
        const tokenCounter = await dynamicSvgNft.getTokenCounter();

        await expect(mintNft).to.emit(dynamicSvgNft, "CreatedNFT");
        assert.equal(tokenCounter.toString(), "1");

        const tokenURI = await dynamicSvgNft.tokenURI(1);
        assert.equal(tokenURI, lowTokenUri);
      });
    });
  });
} else {
}
